mapper.update:
update organization


        org_name = #{orgName},


        org_type = #{orgType},


        address = #{address},


        phone_number = #{phoneNumber}


where org_id = #{orgId}

mapper.select:
select * from (
select rownum rn, organization.* from (
select org_id, org_name, org_type, address, phone_number, created
from organization



        ${column} like #{word}


        org_type in (0, 1)  


        org_type = 0  


        org_type = 1  


        org_type = 9  


order by org_id asc
) organization
)
where rn between #{startRow} and #{endRow}
